var tags;

tags = new function() {
    function markTags() {
        $(".menu-item-object-realizacje1 span").click(function() {
            if ($(this).data("tag") == "wszystkie") {
                if (!$(this).hasClass("-active")) {
                    $(".menu-item-object-realizacje1 span").removeAttr("class");
                    $(this).addClass("-active");
                    $(".realisations-box").show();
                }
            } else {
                $('.menu-item-object-realizacje1 span[data-tag="wszystkie"]').removeAttr("class");
                if ($(this).hasClass("-active")) {
                    $(this).removeAttr("class");
                    $(".realisations-box[data-articletags=' " + $(this).data("tag") + "']").hide();
                    trigger();
                } else {
                    $(this).addClass("-active");
                    console.log($(".menu-item-object-realizacje1 span.-active").length);
                    console.log($(".menu-item-object-realizacje1 span").length);
                    if ($(".menu-item-object-realizacje1 span.-active").length === $(".menu-item-object-realizacje1 span").length - 1) {
                        $(".menu-item-object-realizacje1 span").filter('[data-tag="wszystkie"]').trigger("click");
                    } else {
                        $(".menu-item-object-realizacje1 span").each(function() {
                            if ($(this).hasClass("-active")) {
                                $(".realisations-box[data-articletags=' " + $(this).data("tag") + "']").show();
                            } else {
                                $(".realisations-box[data-articletags=' " + $(this).data("tag") + "']").hide();
                            }
                        });
                    }
                }
            }
            mansory();
        });
    }
    function trigger() {
        if ($(".menu-item-object-realizacje1 span.-active").length == 0) {
            $(".menu-item-object-realizacje1 span").filter('[data-tag="wszystkie"]').trigger("click");
        }
    }
    function mansory() {
        setTimeout(function() {
            var grid = document.querySelector(".row");
            var iso = new Isotope(grid, {
                itemSelector: ".realisations-box",
                masonry: {
                    columnWidth: 200
                }
            });
        }, 0);
    }
    this.init = function() {
        markTags();
    };
}();

$(document).ready(function() {
    tags.init();
    $(".menu-item-object-realizacje1 span.-active").trigger("click");
});

var switchBulb = new function() {
    var greenDiode = $(".switch__top").find(".switch__diode"), redDiode = $(".switch__bottom").find(".switch__diode"), bulb = $(".bulb__image");
    function checked() {
        return $(".switch__input").is(":checked");
    }
    function switchEvent() {
        if (checked() === true) {
            bulb.toggleClass("-active");
            greenDiode.toggleClass("-active");
            redDiode.removeClass("-active");
        } else {
            redDiode.addClass("-active");
        }
    }
    this.init = function() {
        switchEvent();
    };
}();

$(document).ready(function() {
    switchBulb.init();
});

$(".switch").click(function() {
    switchBulb.init();
});

$(document).ready(function() {
    if ($(".switch__input").length > 0) {
        if (annyang) {
            function voiceClickOn() {
                $(".switch__input").prop("checked", true);
                $(".bulb__image").addClass("-active");
                $(".switch__top").find(".switch__diode").addClass("-active");
                $(".switch__bottom").find(".switch__diode").removeClass("-active");
            }
            function voiceClickOff() {
                $(".switch__input").prop("checked", false);
                $(".bulb__image").removeClass("-active");
                $(".switch__top").find(".switch__diode").removeClass("-active");
                $(".switch__bottom").find(".switch__diode").addClass("-active");
            }
            var commands = {
                "turn on": voiceClickOn,
                "turn off": voiceClickOff
            };
            annyang.addCommands(commands);
            annyang.start();
        }
    }
});